import React from 'react';
import './TextArea.css';

function TextArea(props) {
    let Message= props.name;
  return (
    <div className="TextArea">
        <div>
       <textarea> Text </textarea>
       <button>OK</button>
       </div>
       <div>{Message}</div>
    </div>
  );
}

export default TextArea;
