import React from 'react';
import './TextArea.css';

function TextArea(props) {
    let message=props.name;
    let addPost=() => {
    return (message="Text");
    }
  return (
    <div className="TextArea">
        <div>
       <textarea value={message}> </textarea>
       <button onClick={ addPost }>OK</button>
       </div>
  <div>{message}</div>
    </div>
  );
}

export default TextArea;
